  // const [title, setTitle] = useState(props.title);

  // const handlerClick = () => {
  //   setTitle('click!');
  // };

  return (
    <Card className='item-container'>
      <ExpensesDate date={props.date}/>
      <div className='item-description'>
        <h2>{props.title}</h2>
        <div className='item-price'>{props.amount}</div>
      </div>
    </Card>
  );
}

export default ExpenseItem;
