import React, {useState} from 'react';
import '../Style/ExpenseForm.css';

const ExpenseForm = () => {

  //const [enteredTitle, setEnteredTitle] = useState('');
  //const [enteredAmount, setEnteredAmount] = useState('');
  //const [enteredDate, setEnteredDate] = useState('');

  const [userInput, setUserInput] = useState({
    enteredTitle:'',
    enteredAmount:'',
    enteredDate:''
  });

  const handlerChangeTitle = (event) => {
    // console.log(event.target.value)
    setUserInput({
      ...userInput,
      enteredTitle: event.target.value
    });
  };

  const handlerChangeAmount = (event) => {
    // console.log(event.target.value)
    setUserInput({
      ...userInput,
      enteredAmount: event.target.value
    });
  };


  const handlerChangeDate = (event) => {
    // console.log(event.target.value)
    setUserInput({
      ...userInput,
      enteredDate: event.target.value
    });
  };

  console.log(userInput.enteredTitle);

  return (
    <form className='new-expense__inputs'> 
      <div className='new-expense__input'>
        <label>Title</label>
        <input 
          type='text' 
          onChange={handlerChangeTitle}/>
      </div>
      <div className='new-expense__input'>
        <label>Amount</label>
        <input 
          type='number' 
          min='0.01' 
          step='0.01' 
          onChange={handlerChangeAmount}/>
      </div>
      <div className='new-expense__input'>
        <label>Date</label>
        <input 
          type='date'  
          min='2020-01-01' 
          onChange={handlerChangeDate}/>
      </div>
      <div className='new-expense__buttons'>
        <button type='submit'>Add Expense</button>
      </div>
    </form>
  );
}

export default ExpenseForm;
